package controlstatements;

public class SwitchEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int item = 1;
switch(item)
{
case 1 : System.out.println("dhosa");
break;
case 2 :System.out.println("edli");
break;
case 3 :System.out.println("poori");
break;
case 4 :System.out.println("vada");
break;
case 5 :System.out.println("ukma");
break;
case 6 :System.out.println("bonda");
break;
default : System.out.println("invalid");
}
	}

}
